// Seleciona o elemento onde a mensagem será exibida (ou cria um novo)
const messageContainer = document.createElement('div');

// Adiciona estilos ao container da mensagem
messageContainer.style.position = 'fixed';
messageContainer.style.top = '20px';
messageContainer.style.right = '20px';
messageContainer.style.padding = '20px 30px';
// Cor Vermelho #F1416C | Verde $47BE7d | Amarelo #FFC700
messageContainer.style.backgroundColor = '#47BE7d';
messageContainer.style.color = '#fff';
messageContainer.style.borderRadius = '10px';
messageContainer.style.boxShadow = '0 4px 6px rgba(0, 0, 0, 0.1)';
messageContainer.style.fontFamily = 'Inter, sans-serif';
messageContainer.style.fontSize = '14px';
messageContainer.style.zIndex = '1000';
messageContainer.style.opacity = '0'; // Começa invisível
messageContainer.style.transition = 'opacity 1s ease-in-out'; // Transição para animações
messageContainer.innerText = 'Produto cadastrado!';

// Adiciona o elemento ao body
document.body.appendChild(messageContainer);

// Faz a mensagem aparecer com fade-in
setTimeout(() => {
  messageContainer.style.opacity = '1';
}, 10); // Pequeno delay para ativar a transição

// Remove a mensagem após 5 segundos com fade-out
setTimeout(() => {
  messageContainer.style.opacity = '0';
  setTimeout(() => {
    messageContainer.remove();
  }, 1000); // Tempo para a transição de opacidade
}, 3000);

