function numeroPorExtenso(n) {
  const unidades = ['zero', 'um', 'dois', 'três', 'quatro', 'cinco', 'seis', 'sete', 'oito', 'nove'];
  const dezADezenove = ['dez', 'onze', 'doze', 'treze', 'quatorze', 'quinze', 'dezesseis', 'dezessete', 'dezoito', 'dezenove'];
  const dezenas = ['', '', 'vinte', 'trinta', 'quarenta', 'cinquenta', 'sessenta', 'setenta', 'oitenta', 'noventa'];
  const centenas = ['', 'cem', 'cento', 'duzentos', 'trezentos', 'quatrocentos', 'quinhentos', 'seiscentos', 'setecentos', 'oitocentos', 'novecentos'];

  if (n < 0) n = Math.abs(n); // <-- Remove o sinal negativo
  if (n < 10) return unidades[n];
  if (n < 20) return dezADezenove[n - 10];
  if (n < 100) {
    const dezena = Math.floor(n / 10);
    const unidade = n % 10;
    return dezenas[dezena] + (unidade !== 0 ? ' e ' + unidades[unidade] : '');
  }
  if (n < 1000) {
    if (n === 100) return 'cem';
    const centena = Math.floor(n / 100);
    const resto = n % 100;
    return centenas[centena] + (resto !== 0 ? ' e ' + numeroPorExtenso(resto) : '');
  }
  if (n < 1000000) {
    const milhar = Math.floor(n / 1000);
    const resto = n % 1000;
    const milTexto = milhar === 1 ? 'mil' : numeroPorExtenso(milhar) + ' mil';
    return milTexto + (resto !== 0 ? ' e ' + numeroPorExtenso(resto) : '');
  }

  return 'número muito grande';
}

